import React, { useState } from 'react';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [movie, setMovie] = useState(null);

  const generateMovie = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/generate-movie', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt }),
      });
      const data = await response.json();
      console.log('Received data:', data);
      setMovie(data);
    } catch (error) {
      console.error('Error generating movie:', error);
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Movie Generator</h1>
        <div className="prompt-container">
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your movie idea..."
            rows={4}
          />
          <button 
            onClick={generateMovie}
            disabled={loading || !prompt.trim()}
          >
            {loading ? 'Generating...' : 'Generate Movie'}
          </button>
        </div>

        {movie && (
          <div className="movie-container">
            {movie.scenes?.map((scene, index) => (
              <div key={index} className="scene">
                {movie.images[index] && (
                  <img 
                    src={movie.images[index]} 
                    alt={`Scene ${index + 1}`}
                    onError={(e) => {
                      console.error(`Error loading image ${index}:`, e);
                      e.target.src = 'https://via.placeholder.com/400x400?text=Image+Loading+Error';
                    }}
                  />
                )}
                <p className="subtitle">{movie.subtitles[index]}</p>
                <p className="time">{movie.times[index]}s</p>
                <p className="debug">Image URL: {movie.images[index]}</p>
              </div>
            ))}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
