import React, { useState } from 'react';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [movie, setMovie] = useState(null);

  const generateMovie = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/generate-movie', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt }),
      });
      const data = await response.json();
      console.log('Received data:', data);
      setMovie(data);
    } catch (error) {
      console.error('Error generating movie:', error);
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <div className="content">
        <div className="header-section">
          <h1>AI Movie Generator</h1>
          <p className="subtitle-text">Transform your ideas into visual stories</p>
        </div>

        <div className="prompt-section">
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your movie idea... (e.g., 'A story about a robot learning to paint')"
            rows={4}
            className="prompt-input"
          />
          <button 
            onClick={generateMovie}
            disabled={loading || !prompt.trim()}
            className={`generate-button ${loading ? 'loading' : ''}`}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                <span>Creating your movie...</span>
              </>
            ) : (
              'Generate Movie'
            )}
          </button>
        </div>

        {movie && (
          <div className="movie-container">
            <h2>Your Movie Timeline</h2>
            <div className="timeline-container">
              <div className="timeline-track">
                {movie.scenes?.map((scene, index) => (
                  <div key={index} className="timeline-scene">
                    <div className="scene-content">
                      <div className="scene-image-container">
                        <img 
                          src={movie.images[index]} 
                          alt={`Scene ${index + 1}`}
                          onError={(e) => {
                            e.target.src = 'https://via.placeholder.com/400x400?text=Image+Loading+Error';
                          }}
                        />
                        <div className="scene-number">Scene {index + 1}</div>
                      </div>
                      <div className="scene-info">
                        <p className="scene-subtitle">{movie.subtitles[index]}</p>
                        <span className="scene-duration">{movie.times[index]}s</span>
                      </div>
                    </div>
                    {index < movie.scenes.length - 1 && <div className="timeline-connector" />}
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
