import React, { useState } from 'react';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [movie, setMovie] = useState(null);

  const generateMovie = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/generate-movie', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt }),
      });
      const data = await response.json();
      setMovie(data);
    } catch (error) {
      console.error('Error generating movie:', error);
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Movie Generator</h1>
        <div className="prompt-container">
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your movie idea..."
            rows={4}
          />
          <button 
            onClick={generateMovie}
            disabled={loading || !prompt.trim()}
          >
            {loading ? 'Generating...' : 'Generate Movie'}
          </button>
        </div>

        {movie && (
          <div className="movie-container">
            {movie.scenes.map((scene, index) => (
              <div key={index} className="scene">
                <img src={scene.image} alt={`Scene ${index + 1}`} />
                <p className="subtitle">{scene.subtitle}</p>
                <p className="time">{scene.time}s</p>
              </div>
            ))}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
